import React from 'react'

export default function CurrencyRow(prps) {
    const {
        currencyOptions,
        selectedCurrency,
        onChangeCurrency,
        onChangeAmount,
        amount
      } = prps
    return (
        <div>
            <input type="number" className="input"/>
            <select>
            {currencyOptions.map(option => (
            <option key={option} value={option}>{option}</option>
            ))}
            </select>
        </div>
    )
}
